import React, { useState } from 'react';
import './App.css';

const api = {
    key: "dc6ff6a86398fe175602b6f3e0ff0091",
    baseURL: "https://api.openweathermap.org/data/2.5"
}

function App() {
    //Declare and init state to empty str, empty obj
    const [query, setQuery] = useState("");
    const [weather, setWeather] = useState({});

    //Define the search function, takes event object
    const search = (evt) => {
        //If enter key is pressed
        if (evt.key === "Enter") {
            //Call the api
            fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
                .then(res => res.json())
                .then(result => {
                    setWeather(result);
                    setQuery('');
                    console.log(result);
                });
        }
    }
    //Datebuilder function
    const dateBuilder = (d) => {
        let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

        let day = days[d.getDay()];
        let date = d.getDate();
        let month = months[d.getMonth()];
        let year = d.getFullYear();

        return `${day} ${date} ${month} ${year}`
    }
      return (
        <div className="App">
            <main>
                <div className = "search-box">
                    <input
                        type = "text"
                        className= "search-bar"
                        placeholder="Enter something here..."
                    />
                </div>
                <div className= "location-box">
                    <div className= "location">LOCATION USA</div>
                    {/*Call the datebuilder function */}
                    <div className= "date">{dateBuilder(new Date())}</div>
                </div>
                <div className="weather-box">
                    <div className="temp"> 75 Degrees F
                        {/*{Math.round(weather.main.temp)}Â°c*/}
                    </div>
                    <div className="weather"> Lovely
                    </div>
                </div>
            </main>
        </div>
      );
}

export default App;
